import Image from 'next/image'

export default function OptimizedImage({
  src,
  alt,
  width,
  height,
  priority = false,
  ...props
}) {
  return (
    <Image
      src={src}
      alt={alt}
      width={width}
      height={height}
      priority={priority}
      loading={priority ? 'eager' : 'lazy'}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
      {...props}
    />
  )
}